class ErrorBoundry extends Component{
	constructor (props) {
		super(props);
		this.state = {
			hasError: false
		}
	}

componentDidCatch(error, info){
 this.setState({hasError: true})
}

	render(){
		if  (this.state.hasError){
			return  <h1>Sorry an error as occured</h1>
		}
		return this.props.children
	}
}

